mapper.update:
UPDATE notice
SET hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,regdate,name,hit,subject,content
FROM notice
WHERE no=#{no}

mapper.update:
UPDATE notice
SET
	name=#{name},
	subject=#{subject},
	content=#{content}
WHERE no=#{no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM notice
WHERE no=#{no}

mapper.update:
UPDATE notice SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE notice SET
depth=depth+1
WHERE no=#{no}

mapper.select:
SELECT root,depth
FROM notice
WHERE no=#{no}

mapper.update:
UPDATE notice SET
subject=#{subject},content=#{content}
WHERE no=#{no}

UPDATE notice SET
depth=depth-1
WHERE no=#{no}
