mapper.update:
UPDATE qna
SET hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,regdate,name,hit,subject,content
FROM qna
WHERE no=#{no}

mapper.update:
UPDATE qna
SET
	name=#{name},
	subject=#{subject},
	content=#{content}
WHERE no=#{no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM qna
WHERE no=#{no}

mapper.update:
UPDATE qna SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE qna SET
depth=depth+1
WHERE no=#{no}

mapper.select:
SELECT root,depth
FROM qna
WHERE no=#{no}

mapper.update:
UPDATE qna SET
subject=#{subject},content=#{content}
WHERE no=#{no}

UPDATE qna SET
depth=depth-1
WHERE no=#{no}
